CREATE TABLE IF NOT EXISTS `airport`(
    `id` INT AUTO_INCREMENT PRIMARY KEY,
    `code` VARCHAR(20) NOT NULL UNIQUE,
    `name` VARCHAR(300) NOT NULL,
    `category` VARCHAR(100) NOT NULL,
    `location` VARCHAR(300) NOT NULL
);

CREATE TABLE IF NOT EXISTS `flight` (
    `id` INT AUTO_INCREMENT PRIMARY KEY,
    `flight_id` VARCHAR(100) NOT NULL UNIQUE,
    `origin_airport` VARCHAR(20) NOT NULL REFERENCES `airport`(`code`),
    `destination_airport` VARCHAR(20) NOT NULL REFERENCES `airport`(`code`),
    `departure_date` DATETIME NOT NULL,
    `arrival_date` DATETIME NOT NULL,
    `price` FLOAT NOT NULL,
    `remaining_seat` SMALLINT NOT NULL
);

CREATE TABLE IF NOT EXISTS `booking`(
    `id` INT AUTO_INCREMENT PRIMARY KEY,
    `booking_id` VARCHAR(100) NOT NULL UNIQUE,
    `flight_id` VARCHAR(100) NOT NULL REFERENCES `flight`(`flight_id`)
);

CREATE TABLE IF NOT EXISTS `passenger` (
    `id` INT AUTO_INCREMENT PRIMARY KEY,
    `name` VARCHAR(300) NOT NULL,
    `identification` VARCHAR(300) NOT NULL,
    `booking_id` VARCHAR(100) NOT NULL REFERENCES `booking`(`booking_id`)
);

